import React from 'react';
import { Formik } from 'formik';
import * as Yup from 'yup';
import { ThemeProvider } from '@material-ui/styles';
import { createMuiTheme } from '@material-ui/core';
import { FaWhatsapp, FaTelegramPlane } from 'react-icons/fa';
import { RiMessengerLine } from 'react-icons/ri';
import MaskedInput from 'react-text-mask';
import Services from '../../API/Services';

import {
  FormContainer,
  InputText,
  useStyles,
  LineContainer,
  InputSelect,
  SelectContainer,
  SelectLabel,
  SelectOption,
  CheckboxContainer,
  InputCheckbox,
  InputTextArea,
  RadioContainer,
  RadioLabel,
  InputRadio,
  RadioOption,
  CardRadio,
  ButtonRequest,
  TextRequest,
  LabelForm,
  PlatformChooseContainer,
  PlatformOptions,
  PlatformDescription,
} from './styles';
import PlatfomOption from '../PlatfomOption';
import theme from '../../theme';

const RequestServiceForm: React.FC = () => {
  const classes = useStyles();
  const [values, setValues] = React.useState({
    textmask: '(1  )    -    ',
    numberformat: '1320',
  });

  const useTheme = createMuiTheme({
    palette: {
      primary: {
        main: '#3574F2',
      },
    },
  });

  const handleChange = event => {
    setValues({
      ...values,
      [event.target.name]: event.target.value,
    });
  };

  const TextMaskCustom = props => {
    const { inputRef, ...other } = props;

    return (
      <MaskedInput
        {...other}
        ref={ref => {
          inputRef(ref ? ref.inputElement : null);
        }}
        mask={[
          '(',
          /[1-9]/,
          /\d/,
          /\d/,
          ')',
          ' ',
          /\d/,
          /\d/,
          /\d/,
          '-',
          /\d/,
          /\d/,
          /\d/,
          /\d/,
        ]}
        placeholderChar={'\u2000'}
        showMask
      />
    );
  };

  return (
    <FormContainer>
      <ThemeProvider theme={useTheme}>
        <Formik
          onSubmit={() => Services.requestService()}
          initialValues={{
            firstName: '',
            lastName: '',
            email: '',
            phone: '',
            country: '',
            city: '',
            postalCode: '',
            address: '',
            complement: '',
            language: '',
            mandatoryLanguage: null,
            serviceDescription: '',
            urgentService: null,
            contactPlatform: null,
          }}
          validationSchema={Yup.object().shape({
            firstName: Yup.string().required('Fist Name is a required field'),
            lastName: Yup.string().required('Last Name is a required field'),
            email: Yup.string()
              .email('Email is not a valid')
              .required('Email is a required field'),
            phone: Yup.string()
              .min(12, 'Phone number is not a valid')
              .required('Phone is a required field'),
            country: Yup.string().required('Coutry is a required field'),
            city: Yup.string().required('City is a required field'),
            postalCode: Yup.string().required(
              'Postal Code is a required field',
            ),
            address: Yup.string().required('Address is a required field'),
            // complement: Yup.string(),
            language: Yup.string().required('Language is a required field'),
            // mandatoryLanguage: Yup.boolean(),
            // serviceDescription: Yup.string(),
            // urgentService: Yup.boolean(),
            // contactPlatform: Yup.boolean(),
          })}
        >
          {({
            values,
            handleChange,
            setFieldTouched,
            touched,
            errors,
            handleSubmit,
            setFieldValue,
          }) => (
            <>
              <LineContainer>
                <InputText
                  label="First Name"
                  className={classes.inputFirstName}
                  value={values.firstName}
                  onChange={handleChange('firstName')}
                  onBlur={() => setFieldTouched('firstName')}
                  margin="dense"
                  error={!!(errors.firstName && touched.firstName)}
                  helperText={
                    errors.firstName && touched.firstName
                      ? errors.firstName
                      : ''
                  }
                />
                <InputText
                  label="Last Name"
                  className={classes.inputLastName}
                  value={values.lastName}
                  onChange={handleChange('lastName')}
                  onBlur={() => setFieldTouched('lastName')}
                  margin="dense"
                  error={!!(errors.lastName && touched.lastName)}
                  helperText={
                    errors.lastName && touched.lastName ? errors.lastName : ''
                  }
                />
              </LineContainer>
              <LineContainer>
                <InputText
                  label="Phone"
                  className={classes.inputPhone}
                  value={values.phone}
                  onChange={handleChange('phone')}
                  onBlur={() => setFieldTouched('phone')}
                  margin="dense"
                  error={!!(errors.phone && touched.phone)}
                  helperText={errors.phone && touched.phone ? errors.phone : ''}
                />
                <InputText
                  label="Email"
                  className={classes.inputEmail}
                  value={values.email}
                  onChange={handleChange('email')}
                  onBlur={() => setFieldTouched('email')}
                  margin="dense"
                  error={!!(errors.email && touched.email)}
                  helperText={errors.email && touched.email ? errors.email : ''}
                />
              </LineContainer>
              <LineContainer>
                <InputText
                  label="Country"
                  className={classes.inputCountry}
                  value={values.country}
                  onChange={handleChange('country')}
                  onBlur={() => setFieldTouched('country')}
                  margin="dense"
                  error={!!(errors.country && touched.country)}
                  helperText={
                    errors.country && touched.country ? errors.country : ''
                  }
                />
                <SelectContainer className={classes.inputLanguage}>
                  <SelectLabel>Language</SelectLabel>
                  <InputSelect
                    value={values.language}
                    onChange={handleChange('language')}
                    onBlur={() => setFieldTouched('language')}
                    margin="dense"
                    error={!!(errors.language && touched.language)}
                  >
                    <SelectOption value="Ingles">Ingles</SelectOption>
                  </InputSelect>
                </SelectContainer>
                <CheckboxContainer
                  className={classes.inputMandatoryLanguage}
                  control={<InputCheckbox color="primary" />}
                  label="Mandatory Language"
                />
              </LineContainer>
              <LineContainer>
                <InputText
                  label="City"
                  className={classes.inputCity}
                  value={values.city}
                  onChange={handleChange('city')}
                  onBlur={() => setFieldTouched('city')}
                  margin="dense"
                  error={!!(errors.city && touched.city)}
                  helperText={errors.city && touched.city ? errors.city : ''}
                />
                <InputText
                  label="Postal Code"
                  className={classes.inputPostalCode}
                  value={values.postalCode}
                  onChange={handleChange('postalCode')}
                  onBlur={() => setFieldTouched('postalCode')}
                  margin="dense"
                  error={!!(errors.postalCode && touched.postalCode)}
                  helperText={
                    errors.postalCode && touched.postalCode
                      ? errors.postalCode
                      : ''
                  }
                />
              </LineContainer>
              <LineContainer>
                <InputText
                  label="Address"
                  className={classes.inputAddress}
                  value={values.address}
                  onChange={handleChange('address')}
                  onBlur={() => setFieldTouched('address')}
                  margin="dense"
                  error={!!(errors.address && touched.address)}
                  helperText={
                    errors.address && touched.address ? errors.address : ''
                  }
                />
                <InputText
                  label="Complement"
                  className={classes.inputComplement}
                  value={values.complement}
                  onChange={handleChange('complement')}
                  onBlur={() => setFieldTouched('complement')}
                  margin="dense"
                  error={!!(errors.complement && touched.complement)}
                  helperText={
                    errors.complement && touched.complement
                      ? errors.complement
                      : ''
                  }
                />
              </LineContainer>
              <InputTextArea
                label="Service Description"
                multiline
                rows={4}
                fullWidth
                className={classes.inputServiceDescription}
                value={values.serviceDescription}
                margin="dense"
                onChange={handleChange('serviceDescription')}
                onBlur={() => setFieldTouched('serviceDescription')}
                error={
                  !!(errors.serviceDescription && touched.serviceDescription)
                }
                helperText={
                  errors.serviceDescription && touched.serviceDescription
                    ? errors.serviceDescription
                    : ''
                }
              />
              <RadioContainer>
                <RadioLabel>Is a urgent service ?</RadioLabel>
                <InputRadio row>
                  <RadioOption
                    label="Yes"
                    control={<CardRadio color="primary" />}
                    value="yes"
                  />
                  <RadioOption
                    label="No"
                    control={<CardRadio color="primary" />}
                    value="no"
                  />
                </InputRadio>
              </RadioContainer>
              <PlatformChooseContainer>
                <LabelForm>Platform where you prefer to be contacted</LabelForm>
                <PlatformOptions>
                  <PlatfomOption
                    icon={<FaWhatsapp />}
                    color={theme.color.whatsapp}
                  />
                  <PlatfomOption
                    icon={<FaTelegramPlane />}
                    color={theme.color.telegram}
                  />
                  <PlatfomOption
                    icon={<RiMessengerLine />}
                    color={theme.color.messenger}
                  />
                </PlatformOptions>
                <PlatformDescription>
                  *The chosen platform will be where our professional will
                  contact you in order to start the conversation
                </PlatformDescription>
              </PlatformChooseContainer>
              <ButtonRequest
                className={classes.buttonRequest}
                onClick={() => handleSubmit()}
              >
                <TextRequest>Request your quotes</TextRequest>
              </ButtonRequest>
            </>
          )}
        </Formik>
      </ThemeProvider>
    </FormContainer>
  );
};

export default RequestServiceForm;
