import React from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import { ThemeProvider } from '@material-ui/styles';
import { createMuiTheme } from '@material-ui/core';

import {
  FormContainer,
  InputText,
  useStyles,
  LineContainer,
  InputSelect,
  SelectContainer,
  SelectLabel,
  SelectOption,
  CheckboxContainer,
  InputCheckbox,
  InputTextArea,
  RadioContainer,
  RadioLabel,
  InputRadio,
  RadioOption,
  CardRadio,
  ButtonRequest,
  TextRequest,
} from './styles';

const RequestServiceForm: React.FC = () => {
  const classes = useStyles();

  const formik = useFormik({
    initialValues: {
      name: '',
      email: '',
      phone: '',
      country: '',
      city: '',
      postalCode: '',
      address: '',
      complement: '',
      language: '',
      mandatoryLanguage: null,
      serviceDescription: '',
      urgentService: null,
      contactPlatform: null,
    },
    validationSchema: Yup.object().shape({
      name: Yup.string().required('Name is a required field'),
      email: Yup.string()
        .email('Email is not a valid')
        .required('Email is a required field'),
      phone: Yup.string()
        .min(12, 'Phone number is not a valid')
        .required('Phone is a required field'),
      country: Yup.string().required('Coutry is a required field'),
      city: Yup.string().required('City is a required field'),
      postalCode: Yup.string().required('Postal Code is a required field'),
      address: Yup.string().required('Address is a required field'),
      // complement: Yup.string(),
      language: Yup.string().required('Language is a required field'),
      // mandatoryLanguage: Yup.boolean(),
      // serviceDescription: Yup.string(),
      // urgentService: Yup.boolean(),
      // contactPlatform: Yup.boolean(),
    }),
    onSubmit: values => console.log(values),
  });

  const useTheme = createMuiTheme({
    palette: {
      primary: {
        main: '#3574F2',
      },
    },
  });

  return (
    <FormContainer>
      <ThemeProvider theme={useTheme}>
        <LineContainer>
          <InputText
            label="Name"
            className={classes.inputName}
            value={formik.values.name}
            onChange={formik.handleChange('name')}
            onBlur={formik.handleBlur('name')}
            error={!!(formik.errors.name && formik.touched.name)}
            helperText={
              formik.errors.name && formik.touched.name
                ? formik.errors.name
                : ''
            }
          />
        </LineContainer>
        <LineContainer>
          <InputText
            label="Phone"
            className={classes.inputPhone}
            value={formik.values.phone}
            onChange={formik.handleChange('phone')}
            onBlur={formik.handleBlur('phone')}
            error={!!(formik.errors.phone && formik.touched.phone)}
            helperText={
              formik.errors.phone && formik.touched.phone
                ? formik.errors.phone
                : ''
            }
          />
          <InputText
            label="Email"
            className={classes.inputEmail}
            value={formik.values.email}
            onChange={formik.handleChange('email')}
            onBlur={formik.handleBlur('email')}
            error={!!(formik.errors.email && formik.touched.email)}
            helperText={
              formik.errors.email && formik.touched.email
                ? formik.errors.email
                : ''
            }
          />
        </LineContainer>
        <LineContainer>
          <InputText
            label="Country"
            className={classes.inputCountry}
            value={formik.values.country}
            onChange={formik.handleChange('country')}
            onBlur={formik.handleBlur('country')}
            error={!!(formik.errors.country && formik.touched.country)}
            helperText={
              formik.errors.country && formik.touched.country
                ? formik.errors.country
                : ''
            }
          />
          <SelectContainer className={classes.inputLanguage}>
            <SelectLabel id="select-language">Language</SelectLabel>
            <InputSelect
              labelId="select-language"
              value="Ingles"
              onChange={formik.handleChange('language')}
              onBlur={formik.handleBlur('language')}
              error={!!(formik.errors.language && formik.touched.language)}
            >
              <SelectOption>Ingles</SelectOption>
            </InputSelect>
          </SelectContainer>
          <CheckboxContainer
            className={classes.inputMandatoryLanguage}
            control={<InputCheckbox color="primary" />}
            label="Mandatory Language"
          />
        </LineContainer>
        <LineContainer>
          <InputText
            label="City"
            className={classes.inputCity}
            value={formik.values.city}
            onChange={formik.handleChange('city')}
            onBlur={formik.handleBlur('city')}
            error={!!(formik.errors.city && formik.touched.city)}
            helperText={
              formik.errors.city && formik.touched.city
                ? formik.errors.city
                : ''
            }
          />
          <InputText
            label="Postal Code"
            className={classes.inputPostalCode}
            value={formik.values.postalCode}
            onChange={formik.handleChange('postalCode')}
            onBlur={formik.handleBlur('postalCode')}
            error={!!(formik.errors.postalCode && formik.touched.postalCode)}
            helperText={
              formik.errors.postalCode && formik.touched.postalCode
                ? formik.errors.postalCode
                : ''
            }
          />
        </LineContainer>
        <LineContainer>
          <InputText
            label="Address"
            className={classes.inputAddress}
            value={formik.values.address}
            onChange={formik.handleChange('address')}
            onBlur={formik.handleBlur('address')}
            error={!!(formik.errors.address && formik.touched.address)}
            helperText={
              formik.errors.address && formik.touched.address
                ? formik.errors.address
                : ''
            }
          />
          <InputText
            label="Complement"
            className={classes.inputComplement}
            value={formik.values.complement}
            onChange={formik.handleChange('complement')}
            onBlur={formik.handleBlur('complement')}
            error={!!(formik.errors.complement && formik.touched.complement)}
            helperText={
              formik.errors.complement && formik.touched.complement
                ? formik.errors.complement
                : ''
            }
          />
        </LineContainer>
        <InputTextArea
          label="Service Description"
          multiline
          rows={4}
          fullWidth
          className={classes.inputServiceDescription}
          value={formik.values.serviceDescription}
          onChange={formik.handleChange('serviceDescription')}
          onBlur={formik.handleBlur('serviceDescription')}
          error={
            !!(
              formik.errors.serviceDescription &&
              formik.touched.serviceDescription
            )
          }
          helperText={
            formik.errors.serviceDescription &&
            formik.touched.serviceDescription
              ? formik.errors.serviceDescription
              : ''
          }
        />
        <RadioContainer>
          <RadioLabel>Is a urgent service ?</RadioLabel>
          <InputRadio row>
            <RadioOption
              label="Yes"
              control={<CardRadio color="primary" />}
              value="yes"
            />
            <RadioOption
              label="No"
              control={<CardRadio color="primary" />}
              value="no"
            />
          </InputRadio>
        </RadioContainer>
        <ButtonRequest
          className={classes.buttonRequest}
          onClick={() => formik.handleSubmit()}
        >
          <TextRequest>Request your quotes</TextRequest>
        </ButtonRequest>
      </ThemeProvider>
    </FormContainer>
  );
};

export default RequestServiceForm;
