import styled from 'styled-components';
import {
  TextField,
  Select,
  Radio,
  FormControl,
  InputLabel,
  MenuItem,
  Checkbox,
  FormControlLabel,
} from '@material-ui/core';
import { makeStyles } from '@material-ui/styles';
import { Form } from 'formik';
import theme from '../../theme';

export const useStyles = makeStyles(() => ({
  inputName: {
    width: '100%',
  },
  inputPhone: {
    width: '38%',
  },
  inputEmail: {
    width: '60%',
  },
  inputCountry: {
    width: '38%',
  },
  inputLanguage: {
    width: '37%',
  },
  inputMandatoryLanguage: {
    width: '20%',
    justifyContent: 'flex-end',
  },
  inputCity: {
    width: '32%',
  },
  inputPostalCode: {
    width: '32%',
  },
  inputAddress: {
    width: '60%',
  },
  inputComplement: {
    width: '38%',
  },
}));

export const FormContainer = styled.form``;

export const InputText = styled(TextField).attrs({
  id: 'outlined-basic',
  variant: 'outlined',
})`
  background: ${theme.color.lowGrey};
`;

export const SelectContainer = styled(FormControl).attrs({
  variant: 'outlined',
})`
  background: ${theme.color.lowGrey};
`;

export const SelectLabel = styled(InputLabel).attrs({
  id: 'select-language',
})``;

export const InputSelect = styled(Select).attrs({
  labelId: 'select-language',
})``;

export const SelectOption = styled(MenuItem)``;

export const CheckboxContainer = styled(FormControlLabel)`
  color: ${theme.color.highGrey};
`;

export const InputCheckbox = styled(Checkbox)``;

export const InputRadio = styled(Radio)``;

export const LineContainer = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 20px;
`;
