import styled from 'styled-components';
import theme from '../../theme';

export const Container = styled.div<{ width: number | undefined }>`
  display: flex;
  flex: ${props => (props.width ? props.width : 1)};
  flex-direction: column;
`;

export const Title = styled.h3`
  font-size: 12px;
  color: ${theme.color.highGrey};
  font-weight: 500;
  margin-bottom: 8px;
`;

export const InputContainer = styled.div``;

export const Input = styled.input`
  width: 100%;
  border: solid;
  border-width: 2px;
  border-color: ${theme.color.mediumGrey};
  background: ${theme.color.lowGrey};
  border-radius: 10px;
  height: 40px;
  padding: 0px 16px;

  &::placeholder {
    color: ${theme.color.mediumGrey};
    font-size: 14px;
  }
`;

export const ErrorMessage = styled.h3`
  font-size: 10px;
  color: ${theme.color.red};
  font-weight: 500;
`;
