import styled from 'styled-components';

export const Container = styled.div`
  display: flex;
  height: 150px;
  overflow-x: scroll;
  overflow-y: hidden;
  white-space: nowrap;

  &::-webkit-scrollbar {
    display: none;
  }
`;
