import React from 'react';

import { Container, InputContainer, Title, InputMaskCustom } from './styles';

interface Props {
  title: string;
  mask: string;
  placeholder?: string | undefined;
  width?: number;
  onChange?: (event: React.ChangeEvent) => void;
  onBlur?: (event: React.FocusEvent) => void;
  value?: string;
  errorMessage?: string;
}

const InputMask: React.FC<Props> = ({
  title,
  mask,
  placeholder,
  width,
  value,
  onChange,
  onBlur,
  errorMessage,
}) => {
  return (
    <Container width={width}>
      <Title>{title}</Title>
      <InputContainer>
        <InputMaskCustom
          value={value}
          mask={mask}
          maskPlaceholder=""
          placeholder={placeholder}
          onChange={onChange}
          onBlur={onBlur}
        />
        <ErrorMessage>{errorMessage}</ErrorMessage>
      </InputContainer>
    </Container>
  );
};

export default InputMask;
