import React from 'react';
import theme from '../../theme';

import {
  Container,
  DropdownSelect,
  DropdownOption,
  InputCard,
  InputContainer,
  Title,
} from './styles';

interface Props {
  title: string;
  placeholder?: string | undefined;
  width?: number;
  onChange?: (event: React.ChangeEvent) => void;
  onBlur?: (event: React.FocusEvent) => void;
  value?: string;
  errorMessage?: string;
  dataList?: Array<any>;
}

const InputDropdown: React.FC<Props> = ({
  width,
  title,
  placeholder,
  dataList,
}) => {
  return (
    <Container width={width}>
      <Title>{title}</Title>
      <InputContainer>
        <InputCard>
          <DropdownSelect placeholder={placeholder}>
            <DropdownOption
              value=""
              disabled
              style={{ color: theme.color.mediumGrey }}
            >
              Select
            </DropdownOption>
            {dataList?.map(item => (
              <DropdownOption value={item}>{item}</DropdownOption>
            ))}
          </DropdownSelect>
        </InputCard>
      </InputContainer>
    </Container>
  );
};

export default InputDropdown;
