import styled from 'styled-components';
import { MdKeyboardArrowDown } from 'react-icons/md';
import theme from '../../theme';

export const Container = styled.div<{ width: number | undefined }>`
  display: flex;
  flex: ${props => (props.width ? props.width : 1)};
  flex-direction: column;
`;

export const Title = styled.h3`
  font-size: 12px;
  color: ${theme.color.highGrey};
  font-weight: 500;
  margin-bottom: 8px;
`;

export const InputContainer = styled.div`
  height: 50px;
`;

export const InputCard = styled.div`
  width: 100%;
  border: solid;
  border-width: 2px;
  border-color: ${theme.color.mediumGrey};
  background: ${theme.color.lowGrey};
  border-radius: 10px;
  height: 40px;
  padding: 0px 16px;
  display: flex;
  align-items: center;
`;

export const DropdownSelect = styled.select`
  background: transparent;
  border: none;
  appearance: none;
`;

export const DatalistContainer = styled.datalist``;

export const DropdownOption = styled.option`
  background: white;
  color: black;
`;

export const IconDropdown = styled(MdKeyboardArrowDown)``;
