import styled from 'styled-components';
import theme from '../../theme';

export const Container = styled.div`
  padding-top: 100px;
`;

export const Main = styled.div`
  display: flex;
  padding: 60px 120px;
`;

export const TextInformationContainer = styled.div`
  flex: 0.4;
`;

export const Title = styled.h1`
  font-size: 40px;
  font-weight: bold;
  color: ${theme.color.black};
  margin-bottom: 40px;
`;

export const Description = styled.p`
  font-size: 20px;
  font-weight: normal;
  color: ${theme.color.highGrey};
  margin-bottom: 40px;
`;

export const FormContainer = styled.div`
  display: flex;
  flex: 0.6;
  flex-direction: column;
  padding: 0 60px;
`;

export const FormLine = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 20px;
  align-items: center;
`;

export const FormSecondLine = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 20px;
`;

export const MandatoryLanguageContainer = styled.div``;

export const InputTitle = styled.h3`
  font-size: 12px;
  color: ${theme.color.highGrey};
  font-weight: 500;
  margin-bottom: 8px;
`;

export const ServiceDescription = styled.div`
  width: 100%;
`;

export const InputServiceDescription = styled.textarea`
  width: 100%;
  border: solid;
  border-width: 2px;
  border-color: ${theme.color.mediumGrey};
  background: ${theme.color.lowGrey};
  border-radius: 10px;
  height: 100px;
  padding: 16px;

  &::placeholder {
    color: ${theme.color.mediumGrey};
    font-size: 14px;
  }
`;

export const UrgentServiceContainer = styled.div`
  display: flex;
  flex: 1;
`;

export const LabelTitle = styled.h2`
  font-size: 16px;
  color: ${theme.color.highGrey};
  font-weight: 500;
  margin-bottom: 8px;
`;

export const RadioContainer = styled.div`
  display: flex;
  flex: 0.5;
`;
