import React from 'react';
import { IconContext, IconType } from 'react-icons/lib';
import { Container, ButtonPlatform } from './styles';

interface Props {
  icon: JSX.Element;
  color: string;
}

const PlatfomOption: React.FC<Props> = ({ icon, color }) => {
  return (
    <Container>
      <IconContext.Provider value={{ style: { height: 25, width: 25, color } }}>
        <ButtonPlatform color={color}>{icon}</ButtonPlatform>
      </IconContext.Provider>
    </Container>
  );
};

export default PlatfomOption;
