import styled from 'styled-components';

export const Container = styled.div`
  display: flex;
  height: 150px;
  padding-top: 100px;
  overflow-x: scroll;
  overflow-y: hidden;
  white-space: nowrap;
  box-shadow: 0px 5px 10px rgba(0, 0, 0, 0.1);

  &::-webkit-scrollbar {
    display: none;
  }
`;

export const ArrowContainer = styled.div`
  width: 30px;
  background: red;
`;
