import React, { useRef, useState } from 'react';
import { Formik, FormikProps } from 'formik';
import * as Yup from 'yup';
import { ThemeProvider } from '@material-ui/styles';
import { createMuiTheme } from '@material-ui/core';
import { FaWhatsapp, FaTelegramPlane } from 'react-icons/fa';
import { RiMessengerLine } from 'react-icons/ri';
import InputMask from 'react-input-mask';
import Services from '../../API/Services';
import {
  FormContainer,
  InputText,
  useStyles,
  LineContainer,
  InputSelect,
  SelectContainer,
  SelectLabel,
  SelectOption,
  CheckboxContainer,
  InputCheckbox,
  InputTextArea,
  RadioContainer,
  RadioLabel,
  InputRadio,
  RadioOption,
  CardRadio,
  ButtonRequest,
  TextRequest,
  LabelForm,
  PlatformChooseContainer,
  PlatformOptions,
  PlatformDescription,
  SelectError,
} from './styles';
import PlatfomOption from '../PlatfomOption';
import theme from '../../theme';

interface FormValues {
  firstName: string;
  lastName: string;
  email: string;
  phone: string;
  country: string;
  city: string;
  postalCode: string;
  address: string;
  complement?: string;
  language: string;
  mandatoryLanguage?: boolean;
  serviceDescription?: string;
  urgentService?: string;
  contactPlatform?: Array<string>;
}

const RequestServiceForm: React.FC = () => {
  const classes = useStyles();
  const useTheme = createMuiTheme({
    palette: {
      primary: {
        main: '#3574F2',
      },
    },
  });
  const [platforms, setPlatforms] = useState<string[]>([]);
  const formRef = useRef<FormikProps<FormValues>>(null);

  const handleOnSubmit = (values: FormValues) => {
    console.log('values', values);
    console.log('platforms', platforms);
    Services.requestService();
  };

  const handleSetPlatform = (platform: string) => {
    if (platforms.includes(platform)) {
      const index = platforms.indexOf(platform);
      if (index > -1) {
        platforms.splice(index, 1);
      }
      return;
    }
    platforms.push(platform);
    setPlatforms(platforms);
  };

  const handleGoogleAutoComplete = (nativeEvent: any) => {
    console.log('event', nativeEvent.target.value);
    formRef.current?.handleChange('country');
  };

  return (
    <FormContainer>
      <ThemeProvider theme={useTheme}>
        <Formik
          innerRef={formRef}
          onSubmit={values => handleOnSubmit(values)}
          initialValues={{
            firstName: '',
            lastName: '',
            email: '',
            phone: '',
            country: '',
            city: '',
            postalCode: '',
            address: '',
            complement: '',
            language: '',
            mandatoryLanguage: undefined,
            serviceDescription: '',
            urgentService: '',
            contactPlatform: undefined,
          }}
          validationSchema={Yup.object().shape({
            firstName: Yup.string().required('Fist Name is a required field'),
            lastName: Yup.string().required('Last Name is a required field'),
            email: Yup.string()
              .email('Email is not a valid')
              .required('Email is a required field'),
            phone: Yup.string()
              .min(12, 'Phone number is not a valid')
              .required('Phone is a required field'),
            country: Yup.string().required('Coutry is a required field'),
            city: Yup.string().required('City is a required field'),
            postalCode: Yup.string().required(
              'Postal Code is a required field',
            ),
            address: Yup.string().required('Address is a required field'),
            complement: Yup.string(),
            language: Yup.string().required('Language is a required field'),
            mandatoryLanguage: Yup.boolean(),
            serviceDescription: Yup.string(),
            urgentService: Yup.string(),
            contactPlatform: Yup.boolean(),
          })}
        >
          {({
            values,
            handleChange,
            setFieldTouched,
            touched,
            errors,
            handleSubmit,
          }) => (
            <>
              <LineContainer>
                <InputText
                  label="First Name"
                  className={classes.inputFirstName}
                  value={values.firstName}
                  onChange={handleChange('firstName')}
                  onBlur={() => setFieldTouched('firstName')}
                  error={!!(errors.firstName && touched.firstName)}
                  helperText={
                    errors.firstName && touched.firstName
                      ? errors.firstName
                      : ''
                  }
                />
                <InputText
                  label="Last Name"
                  className={classes.inputLastName}
                  value={values.lastName}
                  onChange={handleChange('lastName')}
                  onBlur={() => setFieldTouched('lastName')}
                  error={!!(errors.lastName && touched.lastName)}
                  helperText={
                    errors.lastName && touched.lastName ? errors.lastName : ''
                  }
                />
              </LineContainer>
              <LineContainer>
                <InputMask
                  mask="+999 999 999 999"
                  value={values.phone}
                  onChange={handleChange('phone')}
                  onBlur={() => setFieldTouched('phone')}
                >
                  <InputText
                    label="Phone"
                    className={classes.inputPhone}
                    error={!!(errors.phone && touched.phone)}
                    helperText={
                      errors.phone && touched.phone ? errors.phone : ''
                    }
                  />
                </InputMask>
                <InputText
                  label="Email"
                  className={classes.inputEmail}
                  value={values.email}
                  onChange={handleChange('email')}
                  onBlur={() => setFieldTouched('email')}
