import React from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import {
  createMuiTheme,
  InputLabel,
  Select,
  MenuItem,
} from '@material-ui/core';
import { ThemeProvider } from '@material-ui/styles';

import {
  FormContainer,
  InputText,
  useStyles,
  LineContainer,
  InputSelect,
} from './styles';

const RequestServiceForm: React.FC = () => {
  const classes = useStyles();

  const formik = useFormik({
    initialValues: {
      name: '',
      // email
      phone: '',
      country: '',
      city: '',
      postalCode: '',
      address: '',
      language: '',
      mandatoryLanguage: null,
      serviceDescription: '',
      urgentService: null,
      contactPlatform: null,
    },
    validationSchema: Yup.object().shape({
      name: Yup.string().required('Name is a required field'),
      // email
      phone: Yup.string()
        .min(12, 'Phone number is not a valid')
        .required('Phone is a required field'),
      country: Yup.string().required('Coutry is a required field'),
      city: Yup.string().required('City is a required field'),
      postalCode: Yup.string().required('Postal Code is a required field'),
      address: Yup.string().required('Address is a required field'),
      language: Yup.string().required('Language is a required field'),
      // mandatoryLanguage: Yup.boolean(),
      // serviceDescription: Yup.string(),
      // urgentService: Yup.boolean(),
      // contactPlatform: Yup.boolean(),
    }),
    onSubmit: values => console.log(values),
  });

  const useTheme = createMuiTheme({
    palette: {
      primary: {
        main: '#3574F2',
      },
    },
  });

  return (
    <FormContainer>
      <ThemeProvider theme={useTheme}>
        <LineContainer>
          <InputText label="Name" className={classes.inputName} />
          <InputText label="Phone" className={classes.inputPhone} />
        </LineContainer>
        <LineContainer>
          <InputText label="Email" />
          <FormControl variant="outlined">
            <InputLabel id="demo-simple-select-outlined-label">
              Age23
            </InputLabel>
            <Select
              labelId="demo-simple-select-outlined-label"
              id="demo-simple-select-outlined"
              label="Age12"
            >
              <MenuItem value="">
                <em>None</em>
              </MenuItem>
              <MenuItem value={10}>Ten</MenuItem>
              <MenuItem value={20}>Twenty</MenuItem>
              <MenuItem value={30}>Thirty</MenuItem>
            </Select>
          </FormControl>
        </LineContainer>
        <LineContainer>
          <InputText label="Country" className={classes.inputCEP} />
          <InputText label="City" className={classes.inputCEP} />
          <InputText label="Postal Code" className={classes.inputCEP} />
        </LineContainer>
      </ThemeProvider>
    </FormContainer>
  );
};

export default RequestServiceForm;
