import React from 'react';
import theme from '../../theme';

import {
  Container,
  DropdownSelect,
  DropdownOption,
  InputCard,
  InputContainer,
  Title,
  IconDropdown,
  ErrorMessage,
} from './styles';

interface Props {
  title: string;
  placeholder?: string | undefined;
  width?: number;
  onChange?: (event: React.ChangeEvent) => void;
  onBlur?: (event: React.FocusEvent) => void;
  value?: string;
  errorMessage?: string;
  dataList?: Array<any>;
}

const InputDropdown: React.FC<Props> = ({
  width,
  title,
  placeholder,
  dataList,
  onChange,
  onBlur,
  value,
  errorMessage,
}) => {
  return (
    <Container width={width}>
      <Title>{title}</Title>
      <InputContainer>
        <InputCard>
          <DropdownSelect onChange={onChange} onBlur={onBlur} value={value}>
            <DropdownOption disabled selected>
              {placeholder}
            </DropdownOption>
            {dataList?.map((item, index) => (
              <DropdownOption value={item} key={index}>
                {item}
              </DropdownOption>
            ))}
          </DropdownSelect>
          <IconDropdown />
        </InputCard>
        <ErrorMessage>{errorMessage}</ErrorMessage>
      </InputContainer>
    </Container>
  );
};

export default InputDropdown;
