/* eslint-disable no-console */
import React from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import Header from '../../components/Header';
import InputMask from '../../components/InputMask';
import InputRadio from '../../components/InputRadio';
import InputText from '../../components/InputText';
import {
  Container,
  Main,
  Title,
  Description,
  TextInformationContainer,
  FormContainer,
  FormLine,
  MandatoryLanguageContainer,
  InputTitle,
  InputServiceDescription,
  ServiceDescription,
  LabelTitle,
  RadioContainer,
  UrgentServiceContainer,
  ButtonText,
  RequestButton,
} from './styles';
import InputDropdown from '../../components/InputDropdown';

const RequestService: React.FC = () => {
  const formik = useFormik({
    initialValues: {
      name: '',
      // email
      phone: '',
      country: '',
      city: '',
      postalCode: '',
      address: '',
      language: '',
      mandatoryLanguage: null,
      serviceDescription: '',
      urgentService: null,
      contactPlatform: null,
    },
    validationSchema: Yup.object().shape({
      name: Yup.string().required('Name is a required field'),
      // email
      phone: Yup.string()
        .min(12, 'Phone number is not a valid')
        .required('Phone is a required field'),
      country: Yup.string().required('Coutry is a required field'),
      city: Yup.string().required('City is a required field'),
      postalCode: Yup.string().required('Postal Code is a required field'),
      address: Yup.string().required('Address is a required field'),
      language: Yup.string().required('Language is a required field'),
      mandatoryLanguage: Yup.boolean(),
      serviceDescription: Yup.string(),
      urgentService: Yup.boolean(),
      contactPlatform: Yup.boolean(),
    }),
    onSubmit: values => console.log(values),
  });

  return (
    <>
      <Header minimalist />
      <Container>
        <Main>
          <TextInformationContainer>
            <Title>Needing a Day Laborer?</Title>
            <Description>
              We have thousands of professionals available for your needs, after
              a few moments they will contact you so you can choose the best
              one.
            </Description>
            <Description>
              To help our professional filtering please write a brief
              description about the service.
            </Description>
          </TextInformationContainer>

          <FormContainer>
            <FormLine>
              <InputText
                value={formik.values.name}
                title="Name"
                placeholder="Name"
                width={0.6}
                onChange={formik.handleChange('name')}
                onBlur={formik.handleBlur('name')}
                errorMessage={
                  formik.errors.name && formik.touched.name
                    ? formik.errors.name
                    : ''
                }
              />
              <InputMask
                title="Phone"
                mask="+999 999 999 999"
                placeholder="Phone"
                width={0.3}
                value={formik.values.phone}
                onChange={formik.handleChange('phone')}
                onBlur={formik.handleBlur('phone')}
                errorMessage={
                  formik.errors.phone && formik.touched.phone
                    ? formik.errors.phone
                    : ''
                }
              />
            </FormLine>
            <FormLine>
              <InputDropdown
                title="Country"
                width={0.28}
                placeholder="Country"
              />
              <InputText
                value={formik.values.city}
                onChange={formik.handleChange('city')}
                title="City"
                placeholder="City"
                width={0.28}
                onBlur={formik.handleBlur('city')}
                errorMessage={
                  formik.errors.city && formik.touched.city
                    ? formik.errors.city
                    : ''
                }
              />
              <InputText
                value={formik.values.postalCode}
                onChange={formik.handleChange('postalCode')}
                title="Postal code"
                placeholder="Postal code"
                width={0.28}
                onBlur={formik.handleBlur('postalCode')}
                errorMessage={
                  formik.errors.postalCode && formik.touched.postalCode
                    ? formik.errors.postalCode
                    : ''
                }
              />
            </FormLine>
            <FormLine>
              <InputText
                value={formik.values.address}
                onChange={formik.handleChange('address')}
                title="Address "
                placeholder="Address "
                width={0.4}
                onBlur={formik.handleBlur('address')}
                errorMessage={
                  formik.errors.address && formik.touched.address
                    ? formik.errors.address
                    : ''
                }
              />
              <InputText
                value={formik.values.language}
                onChange={formik.handleChange('language')}
                title="Language"
                placeholder="Language"
                width={0.3}
                onBlur={formik.handleBlur('language')}
                errorMessage={
                  formik.errors.language && formik.touched.language
                    ? formik.errors.language
                    : ''
                }
              />
              <MandatoryLanguageContainer>
                <InputTitle>Mandatory Language</InputTitle>
                <InputRadio />
              </MandatoryLanguageContainer>
            </FormLine>
            <FormLine>
              <ServiceDescription>
                <InputTitle>Service Description</InputTitle>
                <InputServiceDescription placeholder="Describe what you need..." />
              </ServiceDescription>
            </FormLine>
            <FormLine>
              <UrgentServiceContainer>
                <LabelTitle>Is a urgent service ?</LabelTitle>
                <RadioContainer>
                  <InputRadio labelText="Yes" />
                  <InputRadio labelText="No" />
                </RadioContainer>
              </UrgentServiceContainer>
            </FormLine>
            <FormLine>
              <LabelTitle>
                Lugar a ser contactado ( whatsapp, messeger, telegram )
              </LabelTitle>
            </FormLine>
            <RequestButton type="submit" onClick={() => formik.handleSubmit()}>
              <ButtonText>Request your quotes</ButtonText>
            </RequestButton>
          </FormContainer>
        </Main>
      </Container>
    </>
  );
};

export default RequestService;
