import styled from 'styled-components';
import theme from '../../theme';

export const Container = styled.div`
  display: flex;
  align-items: center;
`;

export const RadioContainer = styled.div`
  height: 30px;
  width: 30px;
  border-radius: 20px;
  cursor: pointer;
`;

export const CheckContainer = styled.div`
  height: 25px;
  width: 25px;
  border-radius: 20px;
  background: ${theme.color.primary};
  border: solid;
  border-width: 2px;
  border-color: ${theme.color.primary};
`;

export const NotCheckContainer = styled.div`
  height: 25px;
  width: 25px;
  border-radius: 20px;
  background: ${theme.color.primary};
  border: solid;
  border-width: 2px;
  border-color: ${theme.color.primary};
`;

export const LabelText = styled.h3`
  font-size: 16px;
  font-weight: bold;
  color: ${theme.color.black};
  margin-left: 16px;
`;
